  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTheUser({ ...theUser, [name]: value });
  };

  const updateUser = async () => {
    const userId = await user()._id;

    let response = await Axios.put(`/api/user/${userId}`, theUser);

    response = await Axios.get(`/api/user/${userId}`);

    setTheUser(theUser);
  };

  const mystyle = {
    borderBottom: "none",
    boxShadow: "none",
  };

  return (
    <>
      <Container className="white-text">
        <Row>
          <h3>Profile</h3>
        </Row>
        <Row className="row">
          <Col s={6}>
            <img
              class="materialboxed"
              data-caption="A picture of a way with a group of trees in a park"
              width="250"
              src="https://lorempixel.com/800/400/nature/4"
            ></img>
          </Col>
          <Col s={6}>
            {theUser && (
              <div class="input-field">
                <input
                  style={mystyle}
                  name="fname"
                  value={theUser.fname}
                  type="text"
                  onChange={handleInputChange}
                ></input>
                <label class="active" htmlFor="first_name">
                  First Name
                </label>
              </div>
            )}
            {theUser && (
              <div class="input-field">
                <input
                  style={mystyle}
                  value={theUser.lname}
                  name="lname"
                  type="text"
                  onChange={handleInputChange}
                ></input>
                <label class="active" htmlFor="last_name">
                  Last Name
                </label>
              </div>
            )}
            {theUser && (
              <div class="input-field">
                <input
                  style={mystyle}
                  name="email"
                  value={theUser.username}
                  type="text"
                  onChange={handleInputChange}
                ></input>
                <label class="active" htmlFor="email">
                  Email
                </label>
              </div>
            )}
            {theUser && (
              <p>Joined: {new Date(theUser.created).toDateString()}</p>
            )}
            <Row>
              <Col s={6}>
                <div onClick={updateUser} class="btn teal">
                  Update
                </div>
              </Col>
              <Col s={6}>
                <div class="btn btn-flat red">Delete</div>
              </Col>
            </Row>
          </Col>
        </Row>
        <hr />
        <br />
        <Row className="center-align">
          <h6>Calendar</h6>
        </Row>
        <Row className="center-align">
          <Col s={4}>
            <h6>Favorites movies</h6>
          </Col>
          <Col s={4}>
            <h6>Favorites TV shows</h6>
          </Col>
          <Col s={4}>
            <h6>Favorites Celebrities</h6>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Profile;
